function password() {
	var testV = 1;
	var pass1 = prompt('请输入密码:', '');
	while (testV < 3) {
		if (!pass1) history.go(-1);
		if (pass1 == "David") {
			alert('密码正确!');
			break;
		}
		testV += -1;
		var pass1 = prompt('你能找到密码吗？');
	}
	if (pass1 != "password" & testV == 3) history.go(-1);
	return " ";
}
document.write(password());
